int main( int ac, char** av )
{
	bcm_host_init();
	OMX_Init();

	Camera* camera = new Camera( 1640, 1232, 0, true, 5, true );
	VideoRender* render = new VideoRender( true );

	if ( ac > 1 ) {
		if ( !strcmp( av[1], "a" ) ) {
			camera->setLensShadingGrid( 64, (3280/64)+1, (2464/64)+1, ls_grid );
		}
		if ( !strcmp( av[1], "b" ) ) {
			camera->disableLensShading();
		}
	}

	camera->setFramerate( 40 );
	camera->setSharpness( 100 );
	camera->SetupTunnelVideo( render );

	camera->SetState( Component::StateIdle );
	render->SetState( Component::StateIdle );

	camera->SetState( Component::StateExecuting );
	render->SetState( Component::StateExecuting );
	camera->SetCapturing( true );

	printf( "Ok\n" );

	while ( 1 ) {
		usleep( 1000 * 1000 );
	}

	return 0;
}




